import{_ as e,o as t,c as o,Q as s}from"./chunks/framework.f194f1d8.js";const b=JSON.parse('{"title":"Network Endpoints","description":"","frontmatter":{"head":[["meta",{"property":"og:title","content":"Network Endpoints | Celestia Docs"}],["meta",{"property":"og:description","content":"The first modular blockchain network."}]]},"headers":[],"relativePath":"reference/network-endpoints.md","filePath":"reference/network-endpoints.md","lastUpdated":1741018009000}'),a={name:"reference/network-endpoints.md"},n=s('<h1 id="network-endpoints" tabindex="-1">Network Endpoints <a class="header-anchor" href="#network-endpoints" aria-label="Permalink to &quot;Network Endpoints&quot;">​</a></h1><p>This document serves as the central reference for all network endpoints in the Celestia ecosystem.</p><h2 id="production-rpc-endpoints" tabindex="-1">Production RPC Endpoints <a class="header-anchor" href="#production-rpc-endpoints" aria-label="Permalink to &quot;Production RPC Endpoints&quot;">​</a></h2><p>These RPC providers are meant to be used in production environments.</p><table><thead><tr><th>Provider</th><th>URL</th></tr></thead><tbody><tr><td>Numia</td><td>For RPC access: <a href="https://docs.numia.xyz/infra/overview/getting-started" target="_blank" rel="noreferrer">https://docs.numia.xyz/infra/overview/getting-started</a></td></tr><tr><td>Numia</td><td>For data warehouse access: <a href="https://docs.numia.xyz/sql/querying-data/chains/celestia" target="_blank" rel="noreferrer">https://docs.numia.xyz/sql/querying-data/chains/celestia</a></td></tr><tr><td>Grove</td><td><a href="https://www.grove.city/" target="_blank" rel="noreferrer">https://www.grove.city/</a></td></tr><tr><td>QuickNode</td><td><a href="https://www.quicknode.com/chains/celestia" target="_blank" rel="noreferrer">https://www.quicknode.com/chains/celestia</a></td></tr></tbody></table><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>Do not rely on the free community endpoints listed below for production deployments. Production deployments should rely on service providers with SLAs or your own node.</p></div><h2 id="community-endpoints" tabindex="-1">Community Endpoints <a class="header-anchor" href="#community-endpoints" aria-label="Permalink to &quot;Community Endpoints&quot;">​</a></h2><h3 id="consensus-rpc-endpoints" tabindex="-1">Consensus RPC Endpoints <a class="header-anchor" href="#consensus-rpc-endpoints" aria-label="Permalink to &quot;Consensus RPC Endpoints&quot;">​</a></h3><ul><li><code>public-celestia-rpc.numia.xyz</code></li><li><code>rpc.celestia.pops.one</code></li><li><code>rpc.lunaroasis.net</code></li><li>[Additional endpoints...]</li></ul><h3 id="api-endpoints" tabindex="-1">API Endpoints <a class="header-anchor" href="#api-endpoints" aria-label="Permalink to &quot;API Endpoints&quot;">​</a></h3><ul><li><code>public-celestia-lcd.numia.xyz</code></li><li><code>api.celestia.pops.one</code></li><li><code>api.lunaroasis.net</code></li><li>[Additional endpoints...]</li></ul><h3 id="grpc-endpoints" tabindex="-1">gRPC Endpoints <a class="header-anchor" href="#grpc-endpoints" aria-label="Permalink to &quot;gRPC Endpoints&quot;">​</a></h3><ul><li><code>public-celestia-grpc.numia.xyz</code></li><li><code>grpc.celestia.pops.one</code></li><li><code>grpc.lunaroasis.net:443</code></li><li>[Additional endpoints...]</li></ul><h3 id="websocket-endpoints" tabindex="-1">WebSocket Endpoints <a class="header-anchor" href="#websocket-endpoints" aria-label="Permalink to &quot;WebSocket Endpoints&quot;">​</a></h3><ul><li><code>wss://celestia-ws.chainode.tech:33373/websocket</code></li><li><code>wss://celestia-mainnet-ws.itrocket.net:443/websocket</code></li><li><code>wss://celestia.cumulo.org.es:443/websocket</code></li><li><code>wss://rpc.archive.celestia.cumulo.com.es:443/websocket</code></li></ul><h2 id="data-availability-da-node-endpoints" tabindex="-1">Data Availability (DA) Node Endpoints <a class="header-anchor" href="#data-availability-da-node-endpoints" aria-label="Permalink to &quot;Data Availability (DA) Node Endpoints&quot;">​</a></h2><p>These endpoints are specifically for DA nodes to sync blocks and access state:</p><h3 id="bridge-node-sync-endpoints" tabindex="-1">Bridge Node Sync Endpoints <a class="header-anchor" href="#bridge-node-sync-endpoints" aria-label="Permalink to &quot;Bridge Node Sync Endpoints&quot;">​</a></h3><p>These RPC endpoints allow bridge nodes to sync blocks from the Celestia network. Users need to provide a <code>--core.ip string</code> from a consensus node&#39;s URL or IP that populates a default RPC port at 26657 to their respective DA node.</p><h3 id="state-access-endpoints" tabindex="-1">State Access Endpoints <a class="header-anchor" href="#state-access-endpoints" aria-label="Permalink to &quot;State Access Endpoints&quot;">​</a></h3><p>These gRPC endpoints for DA nodes provide state access for querying the chain&#39;s state and broadcasting transactions (balances, blobs, etc.) to the Celestia network.</p><p>Example usage:</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki github-dark vp-code-dark"><code><span class="line"><span style="color:#B392F0;">celestia</span><span style="color:#E1E4E8;"> </span><span style="color:#F97583;">&lt;</span><span style="color:#9ECBFF;">da_typ</span><span style="color:#E1E4E8;">e</span><span style="color:#F97583;">&gt;</span><span style="color:#E1E4E8;"> </span><span style="color:#9ECBFF;">start</span><span style="color:#E1E4E8;"> </span><span style="color:#79B8FF;">--core.ip</span><span style="color:#E1E4E8;"> </span><span style="color:#F97583;">&lt;</span><span style="color:#9ECBFF;">ur</span><span style="color:#E1E4E8;">l</span><span style="color:#F97583;">&gt;</span><span style="color:#E1E4E8;"> </span><span style="color:#79B8FF;">--core.port</span><span style="color:#E1E4E8;"> </span><span style="color:#F97583;">&lt;</span><span style="color:#9ECBFF;">por</span><span style="color:#E1E4E8;">t</span><span style="color:#F97583;">&gt;</span></span></code></pre><pre class="shiki github-light vp-code-light"><code><span class="line"><span style="color:#6F42C1;">celestia</span><span style="color:#24292E;"> </span><span style="color:#D73A49;">&lt;</span><span style="color:#032F62;">da_typ</span><span style="color:#24292E;">e</span><span style="color:#D73A49;">&gt;</span><span style="color:#24292E;"> </span><span style="color:#032F62;">start</span><span style="color:#24292E;"> </span><span style="color:#005CC5;">--core.ip</span><span style="color:#24292E;"> </span><span style="color:#D73A49;">&lt;</span><span style="color:#032F62;">ur</span><span style="color:#24292E;">l</span><span style="color:#D73A49;">&gt;</span><span style="color:#24292E;"> </span><span style="color:#005CC5;">--core.port</span><span style="color:#24292E;"> </span><span style="color:#D73A49;">&lt;</span><span style="color:#032F62;">por</span><span style="color:#24292E;">t</span><span style="color:#D73A49;">&gt;</span></span></code></pre></div><div class="tip custom-block"><p class="custom-block-title">Bridge nodes</p><p>Not all RPC endpoints guarantee the full block history. Find <a href="https://celestia-tools.brightlystake.com/" target="_blank" rel="noreferrer">an archive endpoint on the community dashboard</a> or run your own consensus node with no pruning for your bridge node.</p></div><h2 id="network-specific-endpoints" tabindex="-1">Network-Specific Endpoints <a class="header-anchor" href="#network-specific-endpoints" aria-label="Permalink to &quot;Network-Specific Endpoints&quot;">​</a></h2><p>For network-specific endpoints (Mainnet Beta, Mocha, Arabica), please refer to:</p><ul><li><a href="/docs-preview/pr-1914/how-to-guides/mainnet">Mainnet Beta</a></li><li><a href="/docs-preview/pr-1914/how-to-guides/mocha-testnet">Mocha Testnet</a></li><li><a href="/docs-preview/pr-1914/how-to-guides/arabica-devnet">Arabica Devnet</a></li></ul>',27),i=[n];function r(l,c,p,d,h,u){return t(),o("div",null,i)}const E=e(a,[["render",r]]);export{b as __pageData,E as default};
