import{_ as e,o as r,c as o,O as a}from"./chunks/framework.61b1c1fe.js";const s="/docs-preview/pr-2318/img/ecosystem-delegation-program.jpg",i="/docs-preview/pr-2318/img/celestia_mammoths_transparent_bg.png",b=JSON.parse('{"title":"Celestia Foundation Ecosystem Delegation Program","description":"","frontmatter":{"head":[["meta",{"property":"og:title","content":"Celestia Foundation Ecosystem Delegation Program | Celestia Docs"}],["meta",{"property":"og:description","content":"The first modular blockchain network."}]]},"headers":[],"relativePath":"community/foundation-ecosystem-delegation-program.md","filePath":"community/foundation-ecosystem-delegation-program.md","lastUpdated":1764080436000}'),n={name:"community/foundation-ecosystem-delegation-program.md"};function d(l,t,m,c,h,p){return r(),o("div",null,[...t[0]||(t[0]=[a('<h1 id="celestia-foundation-ecosystem-delegation-program" tabindex="-1">Celestia Foundation Ecosystem Delegation Program <a class="header-anchor" href="#celestia-foundation-ecosystem-delegation-program" aria-label="Permalink to &quot;Celestia Foundation Ecosystem Delegation Program&quot;">​</a></h1><p><img src="'+s+'" alt="Ecosystem delegation program banner"></p><h2 id="program-overview" tabindex="-1">Program Overview <a class="header-anchor" href="#program-overview" aria-label="Permalink to &quot;Program Overview&quot;">​</a></h2><table><thead><tr><th>Parameter</th><th>Details</th></tr></thead><tbody><tr><td>Cohort size</td><td>10 validators total</td></tr><tr><td>Duration of delegation</td><td>6 months (July 2025–Dec 2025)</td></tr></tbody></table><hr><h2 id="basic-eligibility-compliance" tabindex="-1">Basic Eligibility &amp; Compliance <a class="header-anchor" href="#basic-eligibility-compliance" aria-label="Permalink to &quot;Basic Eligibility &amp; Compliance&quot;">​</a></h2><table><thead><tr><th>Requirement</th><th>Description</th></tr></thead><tbody><tr><td>Validator status</td><td>Active validator on mainnet or testnet</td></tr><tr><td>Commission</td><td>Maximum commission of 20%</td></tr><tr><td>Reliability</td><td>No more than 1 jailing incident in the last 6 months (if more than 1, a public forum post with a detailed post mortem is required)</td></tr><tr><td>Independence</td><td>Not associated with an exchange or custodian</td></tr><tr><td>Compliance</td><td>Not based within the US, within any country subject to economic sanctions, or within any other prohibited jurisdiction, and successfully complete a compliance screen</td></tr><tr><td>Infrastructure</td><td>Maintain a fully archival (non-pruned) bridge node for both Mainnet Beta and Mocha if selected for the program</td></tr><tr><td>Hosting</td><td>Not running your infrastructure in Hetzner or OVH</td></tr><tr><td>Conduct</td><td>Violations of the Celestia.org Code of Conduct will result in immediate disqualification</td></tr></tbody></table><hr><h2 id="form-submission" tabindex="-1">Form Submission <a class="header-anchor" href="#form-submission" aria-label="Permalink to &quot;Form Submission&quot;">​</a></h2><p>Please send your application to this <a href="https://forms.gle/Fbdo66W3GvCiQw896" target="_blank" rel="noreferrer">form</a></p><h2 id="background" tabindex="-1">Background <a class="header-anchor" href="#background" aria-label="Permalink to &quot;Background&quot;">​</a></h2><p>This 6-month pilot runs July–December 2025 and will result in the Celestia Foundation delegating to up to 10 validators. Every applicant must choose exactly one of our three tracks—Mamo-Users, Bootcamp &amp; Mammothon, or R&amp;D—and will be evaluated solely on that focus area. Eligibility and success criteria are defined separately for each track below.</p><p><img src="'+i+'" alt="Celestia Mammoths Cohort Banner"></p><hr><h2 id="mamo-users-rules-requirements" tabindex="-1">Mamo-Users Rules &amp; Requirements <a class="header-anchor" href="#mamo-users-rules-requirements" aria-label="Permalink to &quot;Mamo-Users Rules &amp; Requirements&quot;">​</a></h2><h3 id="objectives-key-metrics" tabindex="-1">Objectives &amp; Key Metrics <a class="header-anchor" href="#objectives-key-metrics" aria-label="Permalink to &quot;Objectives &amp; Key Metrics&quot;">​</a></h3><table><thead><tr><th>Category</th><th>Requirements</th></tr></thead><tbody><tr><td><strong>Recruitment</strong></td><td>• 100 total power users* onboarded by November</td></tr><tr><td><strong>Events &amp; Engagement</strong></td><td>• ≥ 1 app-focused meetup or AMA per month<br>• ≥ 10 attendees at each meetup/AMA<br>• Weekly discussion threads on Discord/forum</td></tr><tr><td><strong>Content Production</strong></td><td>• 6 native-language app tutorials or blog posts (1/month)<br>• 6 review videos of Celestia dApps (1/month)<br>• All high-quality content released under CC0 or equivalent</td></tr><tr><td><strong>Evangelism &amp; Feedback</strong></td><td>• Active promotion of Bootcamp &amp; Mammothon outputs in each report<br>• Aggregate and escalate user feature-requests in Celestia Discord/forum</td></tr><tr><td><strong>Community Health</strong></td><td>• Track retention: power users who contribute (reviews, threads, evangelism)<br>• By Month 6, identify a shortlist of &quot;rising stars&quot; in your local community</td></tr></tbody></table><blockquote><p>*Power User in the Celestia ecosystem is an active, engaged advocate who:</p></blockquote><table><thead><tr><th>Characteristic</th><th>Description</th></tr></thead><tbody><tr><td><strong>Regularly Uses Apps</strong></td><td>Interacts daily or weekly with Celestia-powered applications, test pilots early access features without handholding</td></tr><tr><td><strong>Gives Actionable Feedback</strong></td><td>Opens tutorial threads, participates in Q&amp;A, and responds to surveys to make products better</td></tr><tr><td><strong>Creates &amp; Shares Content</strong></td><td>Publishes high-quality native-language tutorials or short review videos highlighting real-world use</td></tr><tr><td><strong>Evangelizes Authentically</strong></td><td>Recommends apps to peers at meetups or online, acting as an informal ambassador</td></tr><tr><td><strong>Leads Locally</strong></td><td>Organizes testing groups, spawns discussion threads, mentors newcomers, and escalates critical insights to Community Managers</td></tr></tbody></table><table><thead><tr><th>Category</th><th>Details</th></tr></thead><tbody><tr><td><strong>Penalties</strong></td><td>Missing a monthly report or skipping the required event ⇒ immediate disqualification</td></tr><tr><td><strong>Out-of-Scope</strong></td><td>Bootcamp/Mammothon programming, R&amp;D/CIP work, general protocol marketing</td></tr><tr><td><strong>Licensing</strong></td><td>All user-facing content must be released under CC0 (or similarly permissive public-domain terms)</td></tr></tbody></table><h4 id="example-submission-for-mamo-users" tabindex="-1">Example Submission for Mamo-Users <a class="header-anchor" href="#example-submission-for-mamo-users" aria-label="Permalink to &quot;Example Submission for Mamo-Users&quot;">​</a></h4><details><summary>Click to expand example</summary><table><thead><tr><th>Month</th><th>Event Type</th><th>Activities</th></tr></thead><tbody><tr><td><strong>M1</strong></td><td>Virtual AMA</td><td>• Topic: &quot;Which Celestia dApps do you use daily?&quot; (15 attendees)<br>• Gather feedback on existing apps<br>• Discuss how Bootcamp can grant early-alpha access<br>• Publish recap on Discord &amp; Twitter</td></tr><tr><td><strong>M2</strong></td><td>In-person meetup</td><td>• Hands-on testing of three flagship dApps<br>• 12 power users document UX pain points<br>• Collate feedback for Bootcamp teams on how users discover and adopt alphas</td></tr><tr><td><strong>M3</strong></td><td>Virtual AMA</td><td>• Review updated dApp versions<br>• Solicit feature requests and &quot;wish-list&quot; items (20 attendees)<br>• Share a &quot;User Adoption Guide&quot; draft for Bootcamp cohorts</td></tr><tr><td><strong>M4</strong></td><td>In-person meetup</td><td>• Mammothon idea-pitch event<br>• Power users vote on top 5 hackathon themes based on user demand<br>• Publish pitch summaries and vote results on forum + Twitter</td></tr><tr><td><strong>M5</strong></td><td>Virtual AMA</td><td>• Mammothon kickoff—introduce allowlisted ideas<br>• Advise hacker teams on engaging power users for early testing (25 attendees)<br>• Post &quot;How to Reach Your First 50 Users&quot; guide tailored to chosen hackathon themes</td></tr><tr><td><strong>M6</strong></td><td>In-person meetup</td><td>• Preview of early hackathon prototypes<br>• Power users provide live feedback and adoption tactics<br>• Release a &quot;Community Impact Report&quot; detailing recruitment, retention, feedback loops, and rising-star power users</td></tr></tbody></table></details><hr><h2 id="bootcamp-mammothon-rules-requirements" tabindex="-1">Bootcamp &amp; Mammothon Rules &amp; Requirements <a class="header-anchor" href="#bootcamp-mammothon-rules-requirements" aria-label="Permalink to &quot;Bootcamp &amp; Mammothon Rules &amp; Requirements&quot;">​</a></h2><h3 id="scope-deliverables" tabindex="-1">Scope &amp; Deliverables <a class="header-anchor" href="#scope-deliverables" aria-label="Permalink to &quot;Scope &amp; Deliverables&quot;">​</a></h3><table><thead><tr><th>Deliverable</th><th>Description</th></tr></thead><tbody><tr><td><strong>Community Outreach</strong></td><td>Host local meetups (dev &amp; product) to promote Celestia Bootcamp (July–Aug) and Mammothon (Sep–Oct)</td></tr><tr><td><strong>Participant Funnel</strong></td><td>Drive recruited attendees into both programs; celebrate completions with exit events</td></tr><tr><td><strong>Weekly Pit-Stops</strong></td><td>During Bootcamp/Mammothon, run ≥ 1 check-in per week to motivate and troubleshoot</td></tr><tr><td><strong>Translations &amp; Workshops</strong></td><td>Translate in-house learning materials; optionally host validator-onboarding workshops (must notify council)</td></tr></tbody></table><h3 id="timeline-cadence" tabindex="-1">Timeline &amp; Cadence <a class="header-anchor" href="#timeline-cadence" aria-label="Permalink to &quot;Timeline &amp; Cadence&quot;">​</a></h3><table><thead><tr><th>Period</th><th>Activities</th></tr></thead><tbody><tr><td><strong>Jul (Recruitment Warm-Up)</strong></td><td>≥ 1 local promo event per month</td></tr><tr><td><strong>Aug-Sep</strong></td><td>Execute Bootcamp programming &amp; weekly pit-stops</td></tr><tr><td><strong>Oct-Nov</strong></td><td>Run Mammothon hackathon &amp; weekly pit-stops</td></tr><tr><td><strong>Dec</strong></td><td>Wrap up what local talent has delivered as an event</td></tr><tr><td><strong>Reporting</strong></td><td>For each month, deliver a written + social-media report at least 24 h before the B&amp;M council sync</td></tr></tbody></table><h3 id="participation-scale" tabindex="-1">Participation &amp; Scale <a class="header-anchor" href="#participation-scale" aria-label="Permalink to &quot;Participation &amp; Scale&quot;">​</a></h3><table><thead><tr><th>Aspect</th><th>Requirement</th></tr></thead><tbody><tr><td><strong>Recruitment Target</strong></td><td>Recruit ≥ 50 individuals or 10 teams (3–5 members each) per applicant for bootcamps and mammothons participation</td></tr><tr><td><strong>Mentorship Format</strong></td><td>(one-on-one or group) at team’s discretion</td></tr></tbody></table><h3 id="review-process" tabindex="-1">Review Process <a class="header-anchor" href="#review-process" aria-label="Permalink to &quot;Review Process&quot;">​</a></h3><table><thead><tr><th>Component</th><th>Details</th></tr></thead><tbody><tr><td><strong>Monthly B&amp;M Council Sync</strong></td><td>Present outreach stats, pit-stop summaries, and issue resolutions</td></tr><tr><td><strong>Attendance Policy</strong></td><td>Live attendance may be waived if all written reports and community responses are complete</td></tr></tbody></table><h3 id="success-criteria" tabindex="-1">Success Criteria <a class="header-anchor" href="#success-criteria" aria-label="Permalink to &quot;Success Criteria&quot;">​</a></h3><table><thead><tr><th>Criterion</th><th>Target</th></tr></thead><tbody><tr><td><strong>Bootcamp</strong></td><td>≥ 50% of recruited participants complete the program</td></tr><tr><td><strong>Mammothon</strong></td><td>≥ 30% of Bootcamp graduates (and new public sign-ups) finish &amp; submit a built application</td></tr><tr><td><strong>Competition Results</strong></td><td>Produce at least 1 winner (or 2 semi-finalists) in the hackathon</td></tr></tbody></table><table><thead><tr><th>Category</th><th>Details</th></tr></thead><tbody><tr><td><strong>Penalties</strong></td><td>Missing a monthly report or weekly pit-stop ⇒ immediate disqualification</td></tr><tr><td><strong>Out-of-Scope</strong></td><td>• Working with pre-existing, production-grade teams or products<br>• Gambling or unrelated hackathon content</td></tr><tr><td><strong>Licensing</strong></td><td>All created materials must be open-sourced</td></tr></tbody></table><h4 id="example-submission-for-bootcamp-mammothon" tabindex="-1">Example Submission for Bootcamp/Mammothon <a class="header-anchor" href="#example-submission-for-bootcamp-mammothon" aria-label="Permalink to &quot;Example Submission for Bootcamp/Mammothon&quot;">​</a></h4><details><summary>Click to expand example</summary><table><thead><tr><th>Month</th><th>Activities</th></tr></thead><tbody><tr><td><strong>M1</strong></td><td>• Host first local meetup to announce Celestia Bootcamp<br>• Recruit ≥ 20 devs<br>• Translate Week 1 slides<br>• Publish a forum post + tweet<br>• Hold second meetup focused on career pathways through hackathons<br>• Recruit additional ≥ 30 individuals<br>• Finalize pit-stop schedule<br>• Share update on Discord/Twitter</td></tr><tr><td><strong>M2</strong></td><td>• Kick off Bootcamp Month 1<br>• Run weekly pit-stop check-ins<br>• Post attendance stats &amp; participant feedback to forum + socials</td></tr><tr><td><strong>M3</strong></td><td>• Conclude Bootcamp<br>• Host exit celebration event<br>• Report 50%+ completion rate<br>• Gather testimonials<br>• Publish a wrap-up thread</td></tr><tr><td><strong>M4</strong></td><td>• Activate Mammothon<br>• Onboard participants (Bootcamp grads + public)<br>• Facilitate team formations and ideas selection / commitment</td></tr><tr><td><strong>M5</strong></td><td>• Run pit-stops for teams locally<br>• Motivate team progress with help on sharing progress to the broader audience<br>• If applicable, help or be teams&#39; first users</td></tr><tr><td><strong>M6</strong></td><td>• Close Mammothon<br>• Report ≥ 30% finish rate<br>• Open-source all demo projects<br>• Publish final outcomes</td></tr></tbody></table></details><hr><h2 id="r-d-rules-requirements" tabindex="-1">R&amp;D Rules &amp; Requirements <a class="header-anchor" href="#r-d-rules-requirements" aria-label="Permalink to &quot;R&amp;D Rules &amp; Requirements&quot;">​</a></h2><h3 id="scope" tabindex="-1">Scope <a class="header-anchor" href="#scope" aria-label="Permalink to &quot;Scope&quot;">​</a></h3><table><thead><tr><th>Category</th><th>Description</th></tr></thead><tbody><tr><td><strong>CIPs</strong></td><td>Research &amp; propose protocol improvements (e.g. fee-models, staking incentives); produce Celestia Improvement Proposals with simulations or data analysis</td></tr><tr><td><strong>Public-Goods Tooling</strong></td><td>Develop open-sourced libraries/SDKs for JSON-RPC or canonical APIs (e.g. Python, JavaScript), or reusable CLIs for developer workflows</td></tr><tr><td><strong>Duration</strong></td><td>6 months</td></tr><tr><td><strong>Deliverable Volume</strong></td><td>Teams may tackle multiple CIPs and/or tools as they choose</td></tr></tbody></table><h3 id="milestone-reporting" tabindex="-1">Milestone Reporting <a class="header-anchor" href="#milestone-reporting" aria-label="Permalink to &quot;Milestone Reporting&quot;">​</a></h3><table><thead><tr><th>Requirement</th><th>Details</th></tr></thead><tbody><tr><td><strong>Monthly Demo</strong></td><td>Recorded video + written summary published at least 24 h before the R&amp;D council sync</td></tr><tr><td><strong>Feedback Response</strong></td><td>Must address council comments and community feedback on the forum/socials within 2 weeks of receipt</td></tr><tr><td><strong>Communication Channels</strong></td><td>Celestia Forum, X and Discord required; other platforms optional</td></tr></tbody></table><h3 id="review-process-1" tabindex="-1">Review Process <a class="header-anchor" href="#review-process-1" aria-label="Permalink to &quot;Review Process&quot;">​</a></h3><table><thead><tr><th>Component</th><th>Details</th></tr></thead><tbody><tr><td><strong>Monthly R&amp;D Council Sync</strong></td><td>Live attendance may be waived if demos, write-ups, and feedback responses are all complete</td></tr></tbody></table><h3 id="success-criteria-1" tabindex="-1">Success Criteria <a class="header-anchor" href="#success-criteria-1" aria-label="Permalink to &quot;Success Criteria&quot;">​</a></h3><table><thead><tr><th>Area</th><th>Target</th></tr></thead><tbody><tr><td><strong>CIPs</strong></td><td>≥ 1 proposal advances to &quot;beyond Draft&quot; and is featured on a Core Devs call, with clear ownership and resolved feedback</td></tr><tr><td><strong>Tooling</strong></td><td>Adoption by ≥ 1–2 external teams (e.g. Mammothon or Bootcamp cohorts); bonus for third-party startup integration</td></tr><tr><td><strong>Penalties</strong></td><td>Missing a demo/report deadline or failing to address feedback on time ⇒ immediate disqualification</td></tr></tbody></table><h4 id="example-submissions-for-r-d" tabindex="-1">Example Submissions for R&amp;D <a class="header-anchor" href="#example-submissions-for-r-d" aria-label="Permalink to &quot;Example Submissions for R&amp;D&quot;">​</a></h4><details><summary>Click to expand example</summary><p><strong>1. CIP: &quot;Dynamic Data Availability Fees&quot;</strong></p><table><thead><tr><th>Month</th><th>Deliverables</th></tr></thead><tbody><tr><td><strong>M1</strong></td><td>• Publish forum post with proposal outline<br>• Promote via Twitter thread</td></tr><tr><td><strong>M3</strong></td><td>• Share on-chain simulation results<br>• Respond to community Q&amp;A on forum &amp; Discord</td></tr><tr><td><strong>M6</strong></td><td>• Submit final CIP<br>• Present on a Celestia Core Devs call</td></tr></tbody></table><p><strong>2. Tooling: &quot;py-celestia-node-rpc&quot;</strong></p><table><thead><tr><th>Month</th><th>Deliverables</th></tr></thead><tbody><tr><td><strong>M1</strong></td><td>• Repository skeleton + basic JSON-RPC wrapper</td></tr><tr><td><strong>M4</strong></td><td>• Release v1.0 (support subscriptions + tests)<br>• Demo recording + write-up on Discord &amp; Twitter</td></tr><tr><td><strong>M6</strong></td><td>• Enable two Bootcamp teams to build MVPs using the library<br>• Collect adoption feedback</td></tr></tbody></table><p><strong>3. Tooling: &quot;bridge-health-checker&quot;</strong></p><table><thead><tr><th>Month</th><th>Deliverables</th></tr></thead><tbody><tr><td><strong>M1</strong></td><td>• Repo skeleton + CLI that reads DA bridge node disk usage<br>• Forum post + Twitter announcement</td></tr><tr><td><strong>M2</strong></td><td>• Add PagerDuty integration + unit tests + CI<br>• Demo recording + write-up on Discord &amp; Twitter</td></tr><tr><td><strong>M3</strong></td><td>• Onboard 3–5 validators<br>• Collect usage metrics &amp; feedback<br>• Publish adoption report</td></tr></tbody></table></details>',49)])])}const g=e(n,[["render",d]]);export{b as __pageData,g as default};
